function is_zend_encoded($file_path) {\n    $file_contents = file_get_contents($file_path);\n    $signature = "\x5a\x6e\x64\x30"; // Zend Safeguard signature\n    return strpos($file_contents, $signature) !== false;\n}\n\n$file_path = 'path/to/encoded/file.php';\nif (is_zend_encoded($file_path)) {\n    echo "File is encoded by Zend Safeguard";\n} else {\n    echo "File is not encoded by Zend Safeguard";\n}