$str = '';\n$items = array();\n\nif ($_POST['first']) $items[] = $_POST['first'];\nif ($_POST['second']) $items[] = $_POST['second'];\nif ($_POST['third']) $items[] = $_POST['third'];\nif ($_POST['fourth']) $items[] = $_POST['fourth'];\n\nif (count($items) > 1) {\n    $lastItem = array_pop($items);\n    $str = implode(', ', $items) . ' and ' . $lastItem;\n} elseif (count($items) == 1) {\n    $str = $items[0];\n}\n\n$str .= '.';