// Create a mysql connection\n$mysql_link = mysql_connect('localhost', 'username', 'password');\nmysql_select_db('database', $mysql_link);\n\n// Create a mysqli connection\n$mysqli_link = new mysqli('localhost', 'username', 'password', 'database');\n\n// Use the mysql connection\n$result_mysql = mysql_query('SELECT * FROM table', $mysql_link);\n\n// Use the mysqli connection\n$result_mysqli = $mysqli_link->query('SELECT * FROM table');