function formatPostcode($postcode) {\n    // Remove any existing spaces\n    $postcode = preg_replace('/\s+/', '', $postcode);\n    \n    // Use a regular expression to match the postcode pattern\n    if (preg_match('/^([A-Z]{1,2}[0-9R][0-9A-Z]?)([0-9][ABD-HJLNP-UW-Z]{2})$/', $postcode, $matches)) {\n        // Format the postcode with a space\n        return $matches[1] . ' ' . $matches[2];\n    } else {\n        // If the postcode does not match the expected pattern, return the original input\n        return $postcode;\n    }\n}\n\n// Example usage:\n$postcodeInput = 'W14SB';\n$formattedPostcode = formatPostcode($postcodeInput);\necho $formattedPostcode; // Outputs: W1 4SB\n\n$postcodeInput = 'NW26PT';\n$formattedPostcode = formatPostcode($postcodeInput);\necho $formattedPostcode; // Outputs: NW2 6PT\n\n$postcodeInput = 'SG141LB';\n$formattedPostcode = formatPostcode($postcodeInput);\necho $formattedPostcode; // Outputs: SG14 1LB