$args = array(\n    'posts_per_page' => 5,\n    'post_type' => 'post',\n    'tax_query' => array(\n        array(\n            'taxonomy' => 'category',\n            'field'    => 'id',\n            'terms'    => get_the_category()[0]->term_id,\n        ),\n    ),\n);\n\n$related_posts = new WP_Query( $args );\n\nif ( $related_posts->have_posts() ) :\n    while ( $related_posts->have_posts() ) : $related_posts->the_post();\n        // Display the related post title and link\n        the_title( '<li><a href="' . esc_url( get_permalink() ) . '">', '</a></li>' );\n    endwhile;\n    wp_reset_postdata();\nendif;