<?php\nsession_start();\nini_set('display_errors', 1);\nerror_reporting(E_ALL | E_NOTICE);\n\n// Include configuration file\ninclude ("config.inc.php");\n\n// Use prepared statements to prevent SQL injection\n$dsn = 'mysql:host=' . $config['host'] . ';dbname=' . $config['database'];\n$pdo = new PDO($dsn, $config['username'], $config['password']);\n\n// Check if user is logged in\nif (!isset($_SESSION['loggedin']) || $_SESSION['loggedin'] !== '1234') {\n    // Store original URL in session variable\n    $_SESSION['redirect_url'] = $_SERVER['REQUEST_URI'];\n    header('Location: index.php?l=no');\n    exit;\n}\n\n// Login script\n$username = $_POST['username'];\n$password = $_POST['password'];\n\n$stmt = $pdo->prepare('SELECT * FROM clients WHERE username = :username AND password = :password LIMIT 1');\n$stmt->bindParam(':username', $username);\n$stmt->bindParam(':password', $password);\n$stmt->execute();\n\n$data = $stmt->fetch();\n$count = $stmt->rowCount();\n\nif ($count === 1) {\n    $_SESSION['username'] = $username;\n    $_SESSION['password'] = $password;\n    $_SESSION['client_id'] = $data['c_id'];\n    $_SESSION['loggedin'] = '1234';\n\n    // Redirect to original requested page\n    header('Location: ' . $_SESSION['redirect_url']);\n    exit;\n} else {\n    // Handle invalid login\n}