// Insert new row when user clicks +\n$userid = $_SESSION['userid'];\n$rowid = null;\n\n$stmt = $pdo->prepare("INSERT INTO user_data (userid, rowid) VALUES (:userid, NULL)");\n$stmt->bindParam(':userid', $userid);\n$stmt->execute();\n\n$rowid = $pdo->lastInsertId();\n\n// Update row when user enters data\n$data = $_POST['data'];\n$stmt = $pdo->prepare("UPDATE user_data SET data = :data WHERE userid = :userid AND rowid = :rowid");\n$stmt->bindParam(':data', $data);\n$stmt->bindParam(':userid', $userid);\n$stmt->bindParam(':rowid', $rowid);\n$stmt->execute();