function short_hash($input) {\n    $hash = crc32($input);\n    $base62 = '0123456789abcdefghijklmnopqrstuvwxyzABCDEFGHIJKLMNOPQRSTUVWXYZ';\n    $short_hash = '';\n    while ($hash > 0) {\n        $short_hash = $base62[$hash % 62] . $short_hash;\n        $hash = (int)($hash / 62);\n    }\n    return substr($short_hash, 0, 8);\n}\n\n// Example usage:\n$input = 'https://www.example.com/very/long/url';\necho short_hash($input);