<?php\n\n$ftp_server = 'ftp.example.com';\n$ftp_username = 'username';\n$ftp_password = 'password';\n$directory_name = 'dirname_to_delete';\n\n$ftp = ftp_connect($ftp_server);\nif (!$ftp) {\n    die('Could not connect to FTP server');\n}\n\nif (!ftp_login($ftp, $ftp_username, $ftp_password)) {\n    die('Could not login to FTP server');\n}\n\nfunction delete_directories($ftp, $current_dir, $directory_name) {\n    $files = ftp_nlist($ftp, $current_dir);\n    if ($files) {\n        foreach ($files as $file) {\n            if (ftp_is_dir($ftp, $file)) {\n                if (basename($file) == $directory_name) {\n                    ftp_rmdir($ftp, $file);\n                } else {\n                    delete_directories($ftp, $file, $directory_name);\n                }\n            }\n        }\n    }\n}\n\ndelete_directories($ftp, '/', $directory_name);\n\nftp_close($ftp);\n\n?>