// Client-side (PHP program)\n$registrationKey = '...'; // Generated earlier\n$userID = '...'; // Your existing user ID\n$url = 'https://your-joomla-site.com/api/verify-registration-key';\n\n$client = new GuzzleHttp\Client();\n$response = $client->post($url, [\n  'json' => [\n    'registration_key' => $registrationKey,\n    'user_id' => $userID,\n  ],\n]);\n\nif ($response->getStatusCode() === 200) {\n  // Registration key is valid!\n}\n\n// Server-side (Joomla component)\nclass VerifyRegistrationKeyController extends JController\n{\n  public function verify()\n  {\n    $input = JFactory::getApplication()->input;\n    $registrationKey = $input->get('registration_key');\n    $userID = $input->get('user_id');\n\n    // Validate the registration key against the stored values\n    $model = $this->getModel('RegistrationKey');\n    if ($model->validateRegistrationKey($registrationKey, $userID)) {\n      // Registration key is valid!\n      echo json_encode(['success' => true]);\n      exit;\n    }\n\n    // Registration key is invalid\n    echo json_encode(['success' => false]);\n    exit;\n  }\n}