$password = new Zend_Form_Element_Password('password', array(\n    'validators'=> array(\n        'Alnum',\n        array('StringLength', array(6,20))\n    ),\n    'filters'   => array('StringTrim'),\n    'label'     => 'Wachtwoord:'\n));\n\n$password->addFilter(new Ivo_Filters_Sha1Filter());\n\n$password2 = new Zend_Form_Element_Password('password2', array(\n    'validators'=> array(\n        'Alnum',\n        array('StringLength', array(6,20)),\n        array('Identical', array('token' => 'password'))\n    ),\n    'filters'   => array('StringTrim'),\n    'required'  => true,\n    'label'     => 'Wachtwoord:'\n));\n\n// Add a custom validator to compare the hashed values\n$password2->addValidator(new Zend_Validate_Identical(array('token' => $password->getValue())));