$stmt = $db->prepare("SELECT name FROM people WHERE EXISTS (SELECT 1 FROM (SELECT ? AS name) AS tmp WHERE tmp.name = people.name)");\n$stmt->bind_param("s", $name);\n\n$missing_names = array();\nforeach ($foo as $name) {\n    $stmt->execute();\n    $stmt->store_result();\n    if ($stmt->num_rows === 0) {\n        $missing_names[] = $name;\n    }\n}\n\n// $missing_names now contains the strings from $foo that are not in 'people'