$foo = array('John', 'Jane', 'Bob', 'Alice', ...); // your PHP array\n\n// Create a string of comma-separated values from the PHP array\n$foo_str = implode(',', array_map('mysqli_real_escape_string', $foo));\n\n// Query to find the strings in $foo that are not in the 'name' column of 'people'\n$query = "SELECT name FROM people WHERE name NOT IN ('$foo_str')";\n$result = mysqli_query($db, $query);\n\n// Get the results\n$missing_names = array();\nwhile ($row = mysqli_fetch_assoc($result)) {\n    $missing_names[] = $row['name'];\n}\n\n// $missing_names now contains the strings from $foo that are not in 'people'