$pdo = new PDO('mysql:host=localhost;dbname=your_database', 'your_username', 'your_password');\n\n$stmt = $pdo->prepare('\n  SELECT \n    t.TagID, \n    t.Title, \n    COUNT(it.ItemID) as item_count\n  FROM \n    Tag t\n    LEFT JOIN ItemTag it ON t.TagID = it.TagID\n  GROUP BY \n    t.TagID, \n    t.Title\n');\n\n$stmt->execute();\n\n$tags = $stmt->fetchAll(PDO::FETCH_ASSOC);\n\nforeach ($tags as $tag) {\n  echo $tag['Title'] . ' (' . $tag['item_count'] . ')' . PHP_EOL;\n}