// Application 1 (Server)\nuse Slim\Slim;\n\n$app = new Slim();\n\n$app->post('/item', function ($request, $response) {\n    // Process the request and save the item\n    // Return a JSON response\n    return $response->withJson(['message' => 'Item saved successfully'], 201);\n});\n\n$app->run();\n\n// Application 2 (Client)\nuse GuzzleHttp\Client;\n\n$client = new Client();\n\n$response = $client->post('https://application1.com/item', [\n    'json' => ['item' => 'New Item']\n]);\n\nif ($response->getStatusCode() == 201) {\n    echo "Item saved successfully";\n} else {\n    echo "Error saving item";\n}