// Convert .doc to .odt using unoconv\n.doc_to_odt($input_file_path) {\n    $command = "unoconv -f odt $input_file_path";\n    exec($command);\n    // The converted file will have the same name but with an .odt extension\n    return str_replace('.doc', '.odt', $input_file_path);\n}\n\n// Usage\n$input_file_path = 'path/to/your/document.doc';\n$odt_file_path = doc_to_odt($input_file_path);\n\n// Now, convert .odt to XHTML using Odt2Xhtml\nrequire_once 'path/to/Odt2Xhtml.php';\n$odt2xhtml = new Odt2Xhtml($odt_file_path);\n$xhtml_content = $odt2xhtml->getXhtml();\n\n// Do something with $xhtml_content\necho $xhtml_content;