$query = $mysqli->query('SELECT * FROM `people` ORDER BY `name` ASC LIMIT 0,30');\n\n// Measure memory usage before freeing the result\n$mem_before = memory_get_usage();\n\n$query_r = $query->fetch_assoc();\n$query->free();\n\n// Measure memory usage after freeing the result\n$mem_after = memory_get_usage();\n\necho "Memory usage before free(): $mem_before bytes\n";\necho "Memory usage after free(): $mem_after bytes\n";