$fileUrl = 'http://example.com/remote_file.txt';\n$cacheFile = 'local_cache.txt';\n$interval = 86400; // 1 day (in seconds)\n\nif (file_exists($cacheFile) && filemtime($cacheFile) > time() - $interval) {\n    // Cache is valid, read from cache\n    $data = file_get_contents($cacheFile);\n} else {\n    // Cache is outdated or doesn't exist, fetch new data\n    $data = file_get_contents($fileUrl);\n    // Write new data to cache\n    file_put_contents($cacheFile, $data);\n}