function RetrievePageContent($url) {\n    $options = array(\n        'http' => array(\n            'method'  => 'GET',\n            'header'  => 'User-Agent: My Script'\n        )\n    );\n    $context  = stream_context_create($options);\n    $pageContent = file_get_contents($url, false, $context);\n    if ($pageContent === FALSE) {\n        throw new Exception("Failed to retrieve page content");\n    }\n    return $pageContent;\n}