function find_diffs($string1, $string2) {\n    $array1 = preg_split("/\b/", $string1);\n    $array2 = preg_split("/\b/", $string2);\n    $m = count($array1);\n    $n = count($array2);\n    $dp = array_fill(0, $m + 1, array_fill(0, $n + 1, 0));\n    $diffs = array();\n\n    for ($i = 0; $i <= $m; $i++) {\n        for ($j = 0; $j <= $n; $j++) {\n            if ($i == 0) {\n                $dp[$i][$j] = $j;\n            } elseif ($j == 0) {\n                $dp[$i][$j] = $i;\n            } elseif ($array1[$i - 1] == $array2[$j - 1]) {\n                $dp[$i][$j] = $dp[$i - 1][$j - 1];\n            } else {\n                $dp[$i][$j] = min($dp[$i - 1][$j - 1] + 1, $dp[$i - 1][$j] + 1, $dp[$i][$j - 1] + 1);\n            }\n        }\n    }\n\n    $i = $m;\n    $j = $n;\n    while ($i > 0 || $j > 0) {\n        if ($i > 0 && $j > 0 && $array1[$i - 1] == $array2[$j - 1]) {\n            $i--;\n            $j--;\n        } elseif ($i > 0 && ($j == 0 || $dp[$i][$j] == $dp[$i - 1][$j] + 1)) {\n            $diffs[] = array($i - 1, $i, '-');\n            $i--;\n        } elseif ($j > 0 && ($i == 0 || $dp[$i][$j] == $dp[$i][$j - 1] + 1)) {\n            $diffs[] = array($j - 1, $j, '+');\n            $j--;\n        } else {\n            $diffs[] = array($i - 1, $i, '-');\n            $diffs[] = array($j - 1, $j, '+');\n            $i--;\n            $j--;\n        }\n    }\n\n    $diffs[] = array(0, $m, '=');\n    return array($array1, $array2, $diffs);\n}