// separate files for separate concerns (e.g., controller, model, view)\n// use of a framework or OOP principles to organize code\n// clear and concise naming conventions\n\n// UserController.php\nclass UserController {\n    public function index($name) {\n        $user = User::findByName($name);\n        return View::make('user', compact('user'));\n    }\n}\n\n// User.php (model)\nclass User {\n    public static function findByName($name) {\n        // database query using prepared statements\n        $stmt = DB::prepare("SELECT * FROM users WHERE name = ?");\n        $stmt->execute([$name]);\n        return $stmt->fetch();\n    }\n}\n\n// user.blade.php (view)\n<p>Hello, {{ $user->name }}!</p>