<?php\n$twitterUsername = 'your_twitter_username';\n$twitterApiKey = 'your_twitter_api_key';\n$twitterApiSecret = 'your_twitter_api_secret';\n$cacheFile = 'twitter_cache.xml';\n$cacheLifetime = 300; // 5 minutes\n\nif (file_exists($cacheFile) && filemtime($cacheFile) > time() - $cacheLifetime) {\n    // Load cached tweets from XML file\n    $xml = simplexml_load_file($cacheFile);\n    $tweets = $xml->xpath('//item');\n} else {\n    // Fetch new tweets from Twitter API\n    $url = 'https://api.twitter.com/1.1/statuses/user_timeline.json';\n    $params = array(\n        'screen_name' => $twitterUsername,\n        'count' => 10,\n        'trim_user' => true,\n        'exclude_replies' => true\n    );\n    $headers = array(\n        'Authorization: Bearer ' . $twitterApiKey,\n        'Content-Type: application/x-www-form-urlencoded;charset=UTF-8'\n    );\n    $ch = curl_init($url);\n    curl_setopt($ch, CURLOPT_RETURNTRANSFER, true);\n    curl_setopt($ch, CURLOPT_POST, true);\n    curl_setopt($ch, CURLOPT_POSTFIELDS, http_build_query($params));\n    curl_setopt($ch, CURLOPT_HTTPHEADER, $headers);\n    $response = curl_exec($ch);\n    curl_close($ch);\n\n    // Parse JSON response and create XML structure\n    $json = json_decode($response, true);\n    $xml = new SimpleXMLElement('<rss version="2.0"></rss>');\n    foreach ($json as $tweet) {\n        $item = $xml->addChild('item');\n        $item->addChild('title', $tweet['text']);\n        $item->addChild('link', 'https://twitter.com/' . $twitterUsername . '/status/' . $tweet['id_str']);\n    }\n\n    // Save XML to cache file\n    $xml->asXML($cacheFile);\n    $tweets = $xml->xpath('//item');\n}\n\n// Display tweets\nforeach ($tweets as $tweet) {\n    echo '<p>' . $tweet->title . '</p>';\n    echo '<p><a href="' . $tweet->link . '">View on Twitter</a></p>';\n}