function calculate_best_fit($x_values, $y_values) {\n    $n = count($x_values);\n    $x_sum = array_sum($x_values);\n    $y_sum = array_sum($y_values);\n    $x_squared_sum = array_sum(array_map(function($x) { return $x * $x; }, $x_values));\n    $xy_sum = 0;\n    for ($i = 0; $i < $n; $i++) {\n        $xy_sum += $x_values[$i] * $y_values[$i];\n    }\n\n    $m = ($n * $xy_sum - $x_sum * $y_sum) / ($n * $x_squared_sum - $x_sum * $x_sum);\n    $b = ($y_sum - $m * $x_sum) / $n;\n\n    return array($m, $b);\n}\n\n$x_values = array(1, 2, 3, 4, 5);\n$y_values = array(2, 3, 5, 7, 11);\n\nlist($m, $b) = calculate_best_fit($x_values, $y_values);\nprint("y = $m * x + $b");