// When a user logs in, store their session data in a file\n$session_dir = '/path/to/sessions/';\n$session_file = $session_dir . session_id();\nfile_put_contents($session_file, serialize($_SESSION));\n\n// To retrieve all active sessions\n$session_files = scandir($session_dir);\nforeach ($session_files as $file) {\n    $session_data = unserialize(file_get_contents($session_dir . $file));\n    // Do something with the session data\n}