// When a user logs in, store their session data in the database\n$db = new PDO('mysql:host=localhost;dbname=sessions', 'username', 'password');\n\n// Store session data in the database\n$sql = "INSERT INTO sessions (session_id, user_id, data) VALUES (:session_id, :user_id, :data)";\n$stmt = $db->prepare($sql);\n$stmt->bindParam(':session_id', session_id());\n$stmt->bindParam(':user_id', $user_id);\n$stmt->bindParam(':data', serialize($_SESSION));\n$stmt->execute();\n\n// To retrieve all active sessions\n$sql = "SELECT * FROM sessions";\n$stmt = $db->query($sql);\n$sessions = $stmt->fetchAll();\n\n// Loop through each session\nforeach ($sessions as $session) {\n    $session_data = unserialize($session['data']);\n    // Do something with the session data\n}