class MyAclResource extends Zend_Acl_Resource\n{\n    private $_parents = array();\n\n    public function addParent(Zend_Acl_Resource $parent)\n    {\n        $this->_parents[] = $parent;\n    }\n\n    public function getParents()\n    {\n        return $this->_parents;\n    }\n\n    public function inheritsFrom(Zend_Acl_Resource $resource)\n    {\n        foreach ($this->_parents as $parent) {\n            if ($parent === $resource || $parent->inheritsFrom($resource)) {\n                return true;\n            }\n        }\n        return false;\n    }\n}