class Custom_Category_Walker extends Walker_Category {\n    function start_el(&$output, $category, $depth, $args) {\n        $output .= '<li id="cat-' . $category->term_id . '" title="' . esc_attr($category->name) . '">';\n        $output .= '<a id="cat-link-' . $category->term_id . '" title="' . esc_attr($category->name) . '" href="' . esc_url(get_category_link($category->term_id)) . '">' . esc_html($category->name) . '</a>';\n    }\n}\n\n$args = array(\n    'walker' => new Custom_Category_Walker(),\n);\nwp_list_categories($args);