$country = "bahamas"; // example country\n\n$keywords = array("bahamas","island","kingdom","republic","maldives","netherlands",\n                  "isle of man","ivory","philippines","seychelles","usa");\n$exceptions = array("eire","hispaniola");\n\n$country = in_array(strtolower($country), $exceptions) ? $country : \n           (in_array(strtolower($country), $keywords) ? "The " . $country : $country);\n\necho $country;