use GuzzleHttp\Client;\nuse DOMDocument;\n\n$client = new Client();\n\n// Login request\n$response = $client->post('https://rapidshare.com/login', [\n    'form_params' => [\n        'username' => 'your_username',\n        'password' => 'your_password',\n    ],\n]);\n\n// Navigate to the account details page\n$response = $client->get('https://rapidshare.com/account');\n\n// Parse the HTML response\n$dom = new DOMDocument();\n$dom->loadHTML($response->getBody()->getContents());\n\n// Extract the desired information\n$trafficLeft = $dom->getElementsByTagName('span')->item(0)->nodeValue;\n$expiryDate = $dom->getElementsByTagName('span')->item(1)->nodeValue;\n\necho "Traffic left: $trafficLeft\n";\necho "Expiry date: $expiryDate\n";