function get_avatar_file($user_id) {\n    $cache_key = 'avatar_file_' . $user_id;\n    if (apcu_exists($cache_key)) {\n        return apcu_fetch($cache_key);\n    }\n    $file_path = '/path/to/avatars/' . $user_id . '.png';\n    if (file_exists($file_path)) {\n        apcu_store($cache_key, $file_path);\n        return $file_path;\n    }\n    return '/path/to/default/avatar.png'; // fallback to default avatar\n}