function dateTimeFormat($dateIn)\n{\n    $x = explode(" ", $dateIn);\n    $y = explode("-", $x[0]);\n    $z = explode(":", $x[1]);\n\n    $year = $y[0];\n    $month = $y[1];\n    $day = $y[2];\n    $hour = $z[0];\n    $min = $z[1];\n    $sec = $z[2]; // You missed the seconds\n\n    $dateOut = date("F j, Y h:i A", mktime($hour, $min, $sec, $month, $day, $year));\n\n    return $dateOut;\n}