function remove_urls($content) {\n  $urls_to_remove = array('myspace.com'); // add more URLs to remove here\n  $pattern = '/https?:\/\/(' . implode('|', $urls_to_remove) . ')(\/[^\s]*)?/i';\n  return preg_replace($pattern, '', $content);\n}\n\n// Example usage:\n$content = 'Check out my profile on <a href="http://myspace.com/username">Myspace</a>';\n$filtered_content = remove_urls($content);\necho $filtered_content; // outputs: "Check out my profile on "