$xml = new DOMDocument();\n$xml->load('example.xml'); // Load from file\n// or\n$xml->loadXML('<root><element>value</element></root>'); // Load from string\n\n$root = $xml->documentElement;\n\n// Traverse children\nforeach ($root->childNodes as $child) {\n    echo $child->nodeName . ': ' . $child->nodeValue . "\n";\n}\n\n// Get an element by name\n$element = $root->getElementsByTagName('element')->item(0);\necho $element->nodeValue . "\n";\n\n// Create a new element\n$newElement = $xml->createElement('newElement', 'newValue');\n$root->appendChild($newElement);\n\n// Set an attribute\n$element->setAttribute('attrName', 'attrValue');\n\n// Get an attribute\necho $element->getAttribute('attrName') . "\n";\n\n// Use XPath to query the document\n$xpath = new DOMXPath($xml);\n$elements = $xpath->query('//element');\nforeach ($elements as $element) {\n    echo $element->nodeValue . "\n";\n}