class MyPaginatorAdapter implements Zend_Paginator_Adapter_Interface\n{\n    private $model;\n\n    public function __construct(MyModel $model)\n    {\n        $this->model = $model;\n    }\n\n    public function getItems($offset, $itemCountPerPage)\n    {\n        // Use your model to fetch the data with the required offset and limit\n        return $this->model->fetchData($offset, $itemCountPerPage);\n    }\n\n    public function count()\n    {\n        // Use your model to fetch the total count of items\n        return $this->model->fetchTotalCount();\n    }\n}