$stmt = $mysqli->prepare("SELECT * FROM table1 WHERE id = ? AND name = ?");\n$stmt->bind_param("is", $id, $name);\n\n// Get the query string after parameters have been bound\n$query = $stmt->attr_get(MYSQLI_STMT_ATTR_QUERY);\n\n// Replace placeholders with actual values\n$params = array($id, $name);\nforeach ($params as $param) {\n    $query = preg_replace('/\@([a-zA-Z]+)/', $param, $query, 1);\n}\n\necho $query;