trait LoggerTrait {\n    public function __call($method, $args) {\n        Logger::logEntry();\n        Logger::info("Calling method $method with arguments: " . json_encode($args));\n        $result = call_user_func_array([$this, 'do' . ucfirst($method)], $args);\n        Logger::logExit();\n        return $result;\n    }\n}\n\nclass TestClass {\n    use LoggerTrait;\n\n    private function doTester($arg) {\n        // Do some stuff...\n    }\n}