$friends = [2, 4, 6, 8, 10]; // example friend IDs\n$bulletins = [\n    ['bulletinID' => 1, 'userID' => 2],\n    ['bulletinID' => 2, 'userID' => 3],\n    ['bulletinID' => 3, 'userID' => 4],\n    ['bulletinID' => 4, 'userID' => 5],\n    ['bulletinID' => 5, 'userID' => 6],\n    // ... 10,000 bulletins ...\n];\n\n$matchingBulletins = array_uintersect($bulletins, $friends, function($bulletin, $friendID) {\n    return $bulletin['userID'] <=> $friendID;\n});\n\n// Get the first 50 matching bulletin IDs\n$limitedBulletins = array_slice(array_column($matchingBulletins, 'bulletinID'), 0, 50);