$requestMethod = $_SERVER['REQUEST_METHOD'];\n\nswitch ($requestMethod) {\n    case 'GET':\n        // Handle GET request\n        break;\n    case 'POST':\n        // Handle POST request\n        break;\n    case 'PUT':\n        // Handle PUT request\n        break;\n    case 'DELETE':\n        // Handle DELETE request\n        break;\n    default:\n        // Handle unknown request method\n        break;\n}