<?php\n// Configuration\n$host = 'pop.gmail.com';\n$port = 995; // or 110 for non-SSL\n$username = 'your_email@gmail.com';\n$password = 'your_password';\n\n// Connect to POP3 server\n$mailbox = imap_open("{{$host}:$port/pop3}INBOX", $username, $password);\n\n// Get the number of messages\n$num_messages = imap_num_msg($mailbox);\n\n// Loop through each message\nfor ($i = 1; $i <= $num_messages; $i++) {\n    $header = imap_header($mailbox, $i);\n    $body = imap_body($mailbox, $i);\n    // Process the message\n    echo "Subject: " . $header->subject . "\n";\n    echo "From: " . $header->from . "\n";\n    echo "Body: " . $body . "\n\n";\n}\n\n// Close the mailbox\nimap_close($mailbox);\n?>