function calculateStats($data, &$mean, &$stddev) {\n    // calculate mean and stddev\n    $mean = array_sum($data) / count($data);\n    $stddev = sqrt(array_sum(array_map(function($x) use ($mean) { return pow($x - $mean, 2); }, $data)) / count($data));\n}\n\n$data = [1, 2, 3, 4, 5];\n$mean = null;\n$stddev = null;\ncalculateStats($data, $mean, $stddev);\necho "Mean: $mean, StdDev: $stddev";