function getFirstAndLastDate($year, $month) {\n    $firstDate = date('Y-m-d', mktime(0, 0, 0, $month, 1, $year));\n    $lastDate = date('Y-m-d', mktime(0, 0, 0, $month + 1, 0, $year));\n    return array($firstDate, $lastDate);\n}\n\n$year = 2022;\n$month = 7;\nlist($firstDate, $lastDate) = getFirstAndLastDate($year, $month);\necho "First date: $firstDate\n";\necho "Last date: $lastDate\n";