// Create a helper function in a file (e.g., `helpers/MY_url_helper.php`)\nfunction link_to_unless_current($uri, $text) {\n    $CI =& get_instance();\n    $current_uri = $CI->uri->uri_string();\n    if ($current_uri == $uri) {\n        return $text;\n    } else {\n        return anchor($uri, $text);\n    }\n}\n\n// Usage in your view\necho link_to_unless_current('controller/method', 'Link Text');