class Score\n{\n    var $score;\n    var $name;\n    var $dept;\n    var $date;\n\n    function __construct($score, $name, $dept, $date)\n    {\n        $this->score = $score;\n        $this->name = $name;\n        $this->dept = $dept;\n        $this->date = $date;\n    }\n\n    function return_score()\n    {\n        return array(\n            'score' => $this->score,\n            'name' => $this->name,\n            'dept' => $this->dept,\n            'date' => $this->date\n        );\n    }\n}\n\n$newscore = new Score("131313","James", "Marketing", "19/05/2008");\nprint_r($newscore->return_score());