$data = array(\n    'item' => array(\n        'title' => 'Example Title',\n        'description' => 'Example Description',\n        'field1' => 'Value 1',\n        'field2' => 'Value 2',\n        // ...\n    )\n);\n\n$xml = new SimpleXMLElement('<data/>');\n$xml->addChild('item');\nforeach ($data['item'] as $key => $value) {\n    $xml->item->addChild($key, $value);\n}\n\nheader('Content-Type: text/xml');\necho $xml->asXML();