<?php\n\nfunction convertPoToMo($poFile, $moFile) {\n    // Load the .po file\n    $poData = file_get_contents($poFile);\n\n    // Create a gettext catalog\n    $catalog = new gettext_reader($poData);\n\n    // Create a temporary file for the .mo file\n    $tmpFile = tempnam(sys_get_temp_dir(), 'mo');\n\n    // Compile the .po file to a .mo file\n    $catalog->write_mo($tmpFile);\n\n    // Copy the temporary file to the final .mo file\n    copy($tmpFile, $moFile);\n\n    // Remove the temporary file\n    unlink($tmpFile);\n}\n\n// Usage:\n$poFile = 'path/to/file.po';\n$moFile = 'path/to/file.mo';\nconvertPoToMo($poFile, $moFile);\n\n?>