$mysqli = new mysqli("localhost", "user", "password", "database");\n\nif ($mysqli->connect_errno) {\n    die("Failed to connect to MySQL: (" . $mysqli->connect_errno . ") " . $mysqli->connect_error);\n}\n\n$result = $mysqli->query("SHOW TABLES FROM feather") or die($mysqli->error);\n\nwhile($table = $result->fetch_assoc()) {\n    $table = $table['Tables_in_feather'];\n    $stmt = $mysqli->prepare("SELECT COUNT(*) FROM `$table` WHERE `site` LIKE ?");\n    $stmt->bind_param("s", $entry);\n    $stmt->execute();\n    $stmt->bind_result($isThere);\n    $stmt->fetch();\n    if ($isThere) {\n        $table_list[] = $table;\n    }\n}\n\nif (!empty($table_list)) {\n    foreach ($table_list as $table) { \n        $stmt = $mysqli->prepare("SELECT votes_up FROM `$table` WHERE `site` LIKE ?");\n        $stmt->bind_param("s", $entry);\n        $stmt->execute();\n        $stmt->bind_result($votes_up);\n        while ($stmt->fetch()) {\n            $votes[$table] = $votes_up;\n            $sum += $votes_up;\n        } \n    }\n} else {\n    print "";\n}