$query = $this->db->query('\n    SELECT \n        archives.id, \n        archives.signature, \n        type_of_source.description AS type_of_source_description, \n        media_type.description AS media_type_description, \n        origin.description AS origin_description\n    FROM \n        archives, \n        type_of_source, \n        media_type, \n        origin\n    WHERE \n        archives.type_of_source_id = type_of_source.id\n    AND \n        type_of_source.media_type_id = media_type.id\n    AND \n        archives.origin_id = origin.id\n    ORDER BY \n        archives.id ASC\n');