function add_custom_preview_link_meta_box() {\n    add_meta_box('custom_preview_link', 'Custom Preview Link', 'custom_preview_link_meta_box_callback', 'page', 'advanced', 'high');\n}\nadd_action('add_meta_boxes', 'add_custom_preview_link_meta_box');\n\nfunction custom_preview_link_meta_box_callback($post) {\n    // Get the current custom preview link\n    $custom_preview_link = get_post_meta($post->ID, 'custom_preview_link', true);\n    ?>\n    <input type="text" id="custom_preview_link" name="custom_preview_link" value="<?php echo esc_url($custom_preview_link); ?>">\n    <?php\n}\n\nfunction save_custom_preview_link_meta_box($post_id) {\n    // Save the custom preview link\n    update_post_meta($post_id, 'custom_preview_link', $_POST['custom_preview_link']);\n}\nadd_action('save_post', 'save_custom_preview_link_meta_box');