class DAL {\n  // ...\n\n  protected function query($sql) {\n    // Executes the query here and stores the result in $this->query_result\n  }\n\n  public function connect() {\n    // Connects to DB here\n    // Add error handling to make sure the connection was successful\n    if (!$this->conn) {\n      throw new Exception('Failed to connect to database');\n    }\n  }\n\n  // ...\n}\n\nclass myOtherClass extends DAL {\n  public function __construct() {\n    parent::__construct();\n    parent::connect();\n  }\n\n  public function getCountryListBox() {\n    $result = parent::getAllCountries();\n\n    if ($result) {\n      $selectbox = "<select id='countryListBox' name='countryListBox'>";\n\n      // Use a more modern and secure way of fetching data\n      while ($row = $result->fetch_assoc()) {\n        $selectbox .= "<option value='" . htmlspecialchars($row['id']) . "'>" . htmlspecialchars($row['name']) . "</option>";\n      }\n      $selectbox .= "</select>";\n    } else {\n      $selectbox = "Countries could not be retrieved from database.";\n    }\n\n    return $selectbox;\n  }\n}