<?php\nrequire_once 'config.php';\n\n$stmt = $conn->prepare("SELECT * FROM your_table");\n$stmt->execute();\n$rows = $stmt->fetchAll(PDO::FETCH_ASSOC);\n\n$currentIndex = 0;\n\nif (isset($_POST['next'])) {\n    $currentIndex++;\n} elseif (isset($_POST['previous'])) {\n    $currentIndex--;\n}\n\nif ($currentIndex < 0) {\n    $currentIndex = 0;\n} elseif ($currentIndex >= count($rows)) {\n    $currentIndex = count($rows) - 1;\n}\n\n$currentRow = $rows[$currentIndex];\n\n?>\n\n<form action="" method="post">\n    <input type="text" value="<?php echo $currentRow['column1']; ?>">\n    <input type="text" value="<?php echo $currentRow['column2']; ?>">\n    <!-- Add more text boxes for each column -->\n    <button type="submit" name="previous">Previous</button>\n    <button type="submit" name="next">Next</button>\n</form>